using UnityEditor;using UnityEngine;[CustomEditor(typeof(UIBinder))]class UIBinderEditor : Editor{    public override void OnInspectorGUI()    {        UIBinder binder = target as UIBinder;        if (GUILayout.Button("Add Binder To Top Child"))        {            binder.AddBinderToAllTopChild();        }        if (GUILayout.Button("Remove All Top Child Binder"))        {            binder.RemoveAllTopChildBinder();        }        base.OnInspectorGUI();    }}